import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Carousel } from 'react-responsive-carousel';
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader

const MovieDetail = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    const fetchMovie = async () => {
      const response = await axios.get(
        `https://api.themoviedb.org/3/movie/${id}?api_key=74c783d3c0b68d8fa44d7e6d1163bd13`
      );
      setMovie(response.data);
    };

    fetchMovie();
  }, [id]);

  // Assuming posters is an array of movie poster paths for similar movies
  const posters = [movie?.poster_path, 'path2', 'path3', 'path4', 'path5'];

  return movie ? (
    <div className="container glass">
      <h2>{movie.title}</h2>
      <p>{movie.overview}</p>
      <p>Rating: {movie.vote_average}</p>
      <Carousel dynamicHeight={false} showThumbs={false}>
        {posters.map((poster, index) => (
          <div key={index}>
            <img src={`https://image.tmdb.org/t/p/w500/${poster}`} alt={movie.title} />
          </div>
        ))}
      </Carousel>
    </div>
  ) : (
    <div className="loading">Loading...</div>
  );
};

export default MovieDetail;
